local tiles = {	[8061] = {pos={x=32784,y=31175,z=10}, msg="The apparatus in the centre looks odd. You should inspect it!"}, -- entrance	[8056] = {pos={x=32778,y=31172,z=14}, msg="You have a very bad feeling about this..."}, -- corpse	[8062] = {storage=10510, msg="It seems by defeating Azerus you have stopped this army from entering your world! Better leave this ghastly place forever."}  -- army}local function isPortalSealed()	local orb = getTileItemById({x=32783,y=31175,z=10}, 2180)	if(orb.uid > 0)then		return true	end	return falseendfunction onStepIn(cid, item, position, fromPosition)	local tile = tiles[item.uid]	if isPlayer(cid) then		if(item.uid == 8062 and isPortalSealed())then			tile.pos = fromPosition			tile.msg = "The portal has been sealed!"		end		if(tile.pos)then			doTeleportThing(cid, tile.pos)		elseif(tile.storage)then			doCreatureSetStorage(cid, tile.storage, 1)		end		doCreatureSay(cid, tile.msg, TALKTYPE_MONSTER_SAY, false, cid)	end	return trueend